import React, { Component } from "react";
import axios from "axios";

class Auth extends Component {
  constructor(props) {
    super(props);
    this.state = {
      access_token: null,
      error: null,
    };
  }

  handleLogin = () => {
    const username = document.getElementsByName("userName")[0].value;
    const password = document.getElementsByName("password")[0].value;

    axios
      .post("http://195.210.47.82:8000/auth/login/", {
        username: username,
        password: password,
      })
      .then((response) => {
        if (response.data.access_token) {
          console.log("Полученный токен:", response.data.access);
          // Обновляем состояние, чтобы сохранить токен
          this.setState({
            access_token: response.data.access_token,
            error: null,
          });
        } else {
          throw new Error("Токен не был получен");
        }
      })
      .catch((error) => {
        console.error("Ошибка при авторизации:", error);
        this.setState({ error: "Ошибка при авторизации" });
      });
  };

  render() {
    const { error } = this.state;

    return (
      <section className="auth-page">
        <form className="form">
          <section className="form__container">
            <h1 className="form__label">Username</h1>
            <input type="text" className="userName" name="userName" />
            <h1 className="form__label">Password</h1>
            <input type="password" className="password" name="password" />
            <button type="button" className="submit" onClick={this.handleLogin}>
              Войти
            </button>
            {error && <p>Произошла ошибка: {error}</p>}
          </section>
        </form>
      </section>
    );
  }
}

export default Auth;
