import React, { useState, useEffect } from "react";
import axios from "axios";

const Benefits = () => {
  const [facilities, setFacilities] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://195.210.47.82:8000/facility/main/facalities/"
        );
        setFacilities(response.data); // Устанавливаем полученные данные в состояние
      } catch (error) {
        console.error("Ошибка при получении данных:", error);
      }
    };

    fetchData(); // Вызываем функцию для получения данных сразу после монтирования компонента
  }, []); // Пустой массив зависимостей гарантирует выполнение useEffect только один раз

  return (
    <div className="benefits" id="benefits">
      <h1 className="benefits__title">Company Benefits</h1>
      <div className="benefits__container">
        {facilities.map((facility) => (
          <div key={facility.id} className="benefits__item">
            <h2>{facility.name}</h2>
            <p>{facility.count}</p>
            {/* Здесь можно добавить другие поля из данных объекта facility */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Benefits;
