import React, { Component } from "react";
import axios from "axios";

class Auth extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
    };
  }

  handleSubmit = async (event) => {
    event.preventDefault();

    const username = event.target.elements.userName.value;
    const password = event.target.elements.password.value;

    try {
      const response = await axios.post(
        "http://195.210.47.82:8000/auth/login/",
        {
          username: username,
          password: password,
        }
      );

      // Проверяем наличие токена в ответе
      if (response.data.access_token) {
        console.log("Полученный токен:", response.data.access_token);

        // Сохраняем токен в localStorage
        localStorage.setItem("access_token", response.data.access_token);

        // Добавляем токен в заголовок Authorization для всех будущих запросов
        axios.defaults.headers.common[
          "Authorization"
        ] = `Bearer ${response.data.access_token}`;

        // Добавьте здесь обработку полученного токена по вашим требованиям
      } else {
        throw new Error("Токен не был получен");
      }
    } catch (error) {
      console.error("Ошибка при авторизации:", error);
      this.setState({ error: "Ошибка при авторизации" });
    }
  };

  render() {
    const { error } = this.state;

    return (
      <section className="auth-page">
        <form className="form" onSubmit={this.handleSubmit}>
          <section className="form__container">
            <h1 className="form__label">Username</h1>
            <input type="text" className="userName" name="userName" />
            <h1 className="form__label">Password</h1>
            <input type="password" className="password" name="password" />
            <button type="submit" className="submit">
              Войти
            </button>
            {error && <p>Произошла ошибка: {error}</p>}
          </section>
        </form>
      </section>
    );
  }
}

export default Auth;
