import React, { Component } from "react";
import axios from "axios";

class Auth extends Component {
  handleSubmit = (event) => {
    event.preventDefault();

    const username = event.target.elements.userName.value;
    const password = event.target.elements.password.value;

    axios
      .post("http://195.210.47.82:8000/auth/login/", {
        username: username,
        password: password,
      })
      .then((response) => {
        console.log("Полученный токен:", response.data);

        // Сохраняем токен в localStorage
        localStorage.setItem("accessToken", response.data.token);

        // Переход на другую страницу или обновление компонента
        // this.props.history.push("/dashboard"); // Пример перехода на другую страницу

        // Добавьте здесь любую другую логику, зависящую от полученного токена
      })
      .catch((error) => {
        console.error("Ошибка при авторизации:", error);
        // Обработка ошибок авторизации
      });
  };

  componentDidMount() {
    // При монтировании компонента проверяем, есть ли сохранённый токен в localStorage
    const accessToken = localStorage.getItem("accessToken");

    if (accessToken) {
      // Установка токена для всех последующих запросов axios
      axios.defaults.headers.common["Authorization"] = `Bearer ${accessToken}`;
    }
  }

  render() {
    return (
      <section className="auth-page">
        <form className="form" onSubmit={this.handleSubmit}>
          <section className="form__container">
            <h1 className="form__label">Username</h1>
            <input type="text" className="userName" name="userName" />
            <h1 className="form__label">Password</h1>
            <input type="password" className="password" name="password" />
            <button type="submit" className="submit">
              Войти
            </button>
          </section>
        </form>
      </section>
    );
  }
}

export default Auth;
