import React, { Component } from "react";
import axios from "axios";
import Expanded from "../Assets/ExpandedInsurence.jpg";

class MostFQBenefits extends Component {
  constructor(props) {
    super(props);
    this.state = {
      facilities: [],
      token: "", // Initialize token state
    };
  }

  componentDidMount() {
    this.fetchData();
    // Simulate token retrieval (this needs to be implemented based on your authentication flow)
    this.setState({ token: localStorage.getItem("token") }); // Example of retrieving token from localStorage
  }

  fetchData = async () => {
    try {
      const response = await axios.get(
        "http://195.210.47.82:8000/facility/main/"
      );
      this.setState({ facilities: response.data });
      console.log(response.data); // Console log response data for debugging
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  handleBuyClick = async (facilityId) => {
    const { token } = this.state;
    try {
      const response = await axios.post(
        `http://195.210.47.82:8000/facility/main/facility/${facilityId}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`, // Include token in the Authorization header
          },
        }
      );
      console.log("Buy request response:", response.data); // Handle response as needed
    } catch (error) {
      console.error("Error while buying:", error);
    }
  };

  render() {
    const { facilities } = this.state;
    return (
      <section className="programs" id="programs">
        {facilities.length > 0 ? (
          <div className="programs__container">
            {facilities.map((facility) => (
              <div key={facility.id} className="programs__item">
                <h2 className="programs__title">{facility.name}</h2>
                <h2 className="programs__item--title">
                  {facility.facilities[0].name}
                </h2>
                <h2 className="programs__item--description">
                  {facility.facilities[0].description}
                </h2>
                <button
                  className="programs__item--buy"
                  onClick={() => this.handleBuyClick(facility.id)}
                >
                  Buy
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </section>
    );
  }
}

export default MostFQBenefits;
